mapper.select:
		select * from user where 1=1

            	and userName like CONCAT('%',#{userName},'%') 


            	and compName = #{compName}


            	and compTel = #{compTel}


            	and contactName = #{contactName}


            	and contactTel = #{contactTel}


            	and auth = #{auth}


            	and userState = #{userState}
